--유효(조건만족시 추가)
--조건만족시에만 insert

--sysdate = 월요일일때 insert
    from dual where to_char(sysdate, 'd') = '2';

--주말일때
    from dual where to_char(sysdate, 'd') = '7' OR to_char(sysdate, 'd') = '1';
    
--평일일때
    from dual where to_char(sysdate, 'd') != '7' and to_char(sysdate, 'd') != '1' and to_char(sysdate, 'd') != '2';


