insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서관 회원 가입을 하고 싶습니다. 회원가입 시 필요한 것은 무엇인가요?', '회원가입은 홈페이지에서 진행됩니다. 인터넷으로 회원가입 후 신분증(주민등록증, 운전면허증, 중·고등학생-학생증)을 지참 후 도서관으로 방문하시어 확인 후 승인처리해 드리며, 회원카드 발급도 가능합니다.(신규회원 무료) 회원카드 발급은 하절기(3월~10월) 평일 19:30, 동절기(11월~2월) 평일 18:30 , 주말 16:00 까지입니다.', '회원가입');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '비밀번호가 생각이 나질 않습니다. 어떻게 해야 하나요?', '홈페이지 상의 비밀번호 재발급(ID/PW 찾기)을 이용하시면 변경 가능합니다.
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서 검색을 했을 때는 대출 가능이라고 나오는데 서가에는 없습니다.', '다른 이용자가 자료실 내에서 열람하고 있거나 해당 도서가 제자리에 꽂혀 있지 않은 경우 대출 가능 상태임에도 서가에 없을 수 있습니다. 각 자료실 사서에게 문의하시면 책의 소재에 대해 파악할 수 있습니다.', '도서관자료');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서관에서 대출이 되지 않는 자료는 어떤 것들이 있나요?', '많은 분들이 이용하는 도서관에서는 대출에 제한이 되는 자료가 있습니다. 참고도서, 직원업무용도서, 연속간행물실 자료, 참고도서(신문, 지도 등), 잡지 신규자료도 제한하고 있습니다. 모든 자료는 도서관 내에서 열람하실 수 있습니다.', '도서관자료');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '잡지는 대출할 수 있나요?', '드래곤도서관 종합자료실/어린이실에 비치된 신문과 잡지는 대출하실 수 없습니다.', '도서관이용');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '최근에 출판된 책을 읽고 싶은데 신간도서를 별도로 관리하고 있는지 알고 싶습니다.', '도서관에 새로 입수된 자료는 홈페이지 ‘자료찾기 - 신착자료’에서 자료의 입수일자와 소장위치 등을 확인하실 수 있습니다. 또한 각 자료열람실의 신착도서코너를 통해 자료실에 입수되는 신간 도서들을 살펴볼 수 있으며, 홈페이지 ''신간도서코너''를 통해 수서 담당자가 추천하는 주목할만한 신간 도서들을 안내받을 수 있습니다.', '도서관이용');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '오늘 반납한 책을 오늘 바로 대출할 수 있나요?', '반납하신 책은 예약자료가 아닌 경우 당일 재대출이 가능합니다.', '도서관이용');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '인쇄(프린트)를 할 수 있나요?', '드래곤도서관 3층 종합자료실 문서작업코너에 프린터(흑백 전용)가 설치되어 인쇄를 하실 수 있습니다.
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서관에서 물건(지갑, 책 등)을 잃어버렸습니다. 어떻게 하나요?', '도서관에서 물건을 분실하거나 습득한 이용자께서는 반드시 3층 종합자료실 데스크에 신고하여야 합니다. 도서관에 신고된 분실물은 물건을 찾게 되면 신고하신 이용자께 연락을 드립니다. 도서관 방문 시에는 신분증을 지참하시기 바랍니다.', '기타');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '홈페이지 상에서 왜 개인정보 수정이 안되나요?', '홈페이지 로그인 후 나만의도서관/ 개인정보관리에서 수정 가능합니다. 다만 주요 정보사항은 수정 불가능할 수 있습니다. 또한 도서관에 방문하셔서 정해진 절차에 따라 변경 신청도 가능합니다.', '기타');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '대출 반납기일이 되어 가는데 아직 책을 다 보지 못했습니다. 대출기간을 연장할 수도 있나요?', '대출기간 연장은 반납기일 이전에 신청해야하며, 대출 익일부터 1회에 한해, 반납예정일로부터 1주일 연장가능 합니다. 단, 연장을 희망하는 도서가 타 회원의 예약도서일 경우 연장이 불가합니다.', '도서관자료');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '책을 빌려서 보고 싶습니다. 대출절차는 어떻게 되며, 몇 권까지 대출할 수 있나요?', '대출자료의 연장 신청방법은 다음과 같습니다.', '도서관이용');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '질문전자책 / 오디오북 사용방법을 알려주세요.', '1. 홈페이지의 회원 가입 신청 시 작성한 아이디와 비밀번호로 로그인을 합니다. 메뉴에서 "대출연장/예약조회"를 클릭하면 현재 대출한 도서 항목이 나오는데 여기서 "연장신청"을 선택하면 됩니다. 2. 직접 방문해서 해당 자료열람실의 담당자에게 신청하거나 전화로도 신청 가능합니다.', '기타');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서를 반납하고 다시 대출하고 싶습니다.', '정회원(성북구민), 준회원(타지역구민) 관계없이 각 도서관별 5권, 통합 20권까지 대출이 가능합니다. 대출기간은 14일간(2주)이며 대출 신청 시에는 반드시 본인 회원증을 소지하셔야 합니다. ', '기타');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '책을 예약했습니다. 어떻게 대출해야 하나요?', '대출은 참고자료, 수험서, 연속간행물을 제외한 모든 도서에 대해 가능합니다.', '회원가입');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '대출한 도서의 반납기일이 지나게 되면(대출도서가 연체되면) 어떻하나요?', '1) 성북구립도서관 홈페이지 회원가입 2) 홈페이지 좌측 하단의 ''디지털라이브러리'' > ''e-bqqk'' / ''오디오북'' 클릭하여 이용', '회원가입');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '대출 반납기일이 되어 가는데 아직 책을 다 보지 못했습니다. 대출기간을 연장할 수도 있나요?', '예약하신 도서는 반납되는 즉시 예약자에게 휴대폰 SMS를 통해 ‘도착통보’가 이루어집니다. ', '기타');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서대출회원에 가입하려면 어떻게 해야 되나요?', ' 홈페이지 로그인 후, ‘대출예약/예약조회’의 [예약상황조회]에서도 도서상태를 확인하실 수 있습니다. 도착통보 문자를 받으신 후 3일 이내에 도서관을 방문, 각 열람실 안내데스크에서 예약도서를 대출하시면 됩니다.', '도서관자료');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서대출 회원증을 재발급 받으려면 어떻게 해야 하나요?', '도서대출은 회원 본인만 가능하므로, 예약도서 또한 예약자 본인만 대출 가능합니다. (가족회원으로 등록되어 있다면, 본인 카드와 해당 가족 카드지참)', '회원가입');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '대출할 수 있는 책의 수, 대출기간을 알고 싶습니다.', '예를 들어 도서를 2권을 대출하셨다가 반납기일을 1주일간 지연하게 되면, “7일(연체일수)”간 도서의 대출이 제한됩니다.', '도서관이용');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서관에서 빌린 책을 기한 내에 반납하지 못할것 같습니다. 어떻게 해야 하나요?', '대출기간 연장은 반납기일 이전에 신청해야하며, 대출 익일부터 1회에 한해, 반납예정일로부터 1주일 연장가능 합니다. 단, 연장을 희망하는 도서가 타 회원의 예약도서일 경우 연장이 불가합니다.', '회원가입');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '희망도서 신청은 어떻게 해야하나요?', '1. 홈페이지의 회원 가입 신청 시 작성한 아이디와 비밀번호로 로그인을 합니다. 메뉴에서 "대출연장/예약조회"를 클릭하면 현재 대출한 도서 항목이 나오는데 여기서 "연장신청"을 선택하면 됩니다. 2. 직접 방문해서 해당 자료열람실의 담당자에게 신청하거나 전화로도 신청 가능합니다.', '회원가입');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '대출한 책을 가까운 도서관에 반납할 수 있나요?', '대출권수는 20권이며 대출기간은 14일입니다. (도서관별 대출권수는 다를 수 있습니다.)', '도서관이용');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서관 이용시간이 어떻게 되나요?', '동일도서를 구해 오셔야 합니다. 서명은 물론 저자명, 출판사, 출판년, 형태 등이 동일해야 합니다. 만약 동일도서를 구하기 어려울 경우에는 현금으로 변상하실 수 있습니다.', '도서관자료');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '자유열람실이 있나요?', '도서관 홈페이지 희망도서신청 코너를 이용하시면 됩니다. 반드시 도서대출회원으로 가입되어 있어야 합니다.', '회원가입');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '노트북 사용에 관하여 알고 싶습니다.', '1회에 한하여 7일간 연기 가능하며 반납일 하루 전이나 반납당일에 전화 또는 홈페이지에서 연기 가능합니다. 단, 예약도서인 경우에는 연기할 수 없습니다.', '회원가입');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서 대출에 관하여 알고 싶어요', '대출한 책은 어디에서나 원하는 도서관으로 반납하실 수 있습니다.', '회원가입');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '무선인터넷(WI-FI) 가 구축되어 있나요?', '휴관일:  매주 월요일 및 정부지정 공휴일, 기타 관장이 필요하다고 인정하는 날', '기타');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '다른 도서관의 책도 반납이 가능한가요?', '도서를 분실 혹은 훼손하였을 경우 동일한 자료료 변상하셔야 합니다.', '도서관이용');
insert into tblNoticeFAQ (faq_seq, faq_subject, faq_content, faq_kind) values (seqNoticeFAQ.nextVal, '도서를 분실했는데 어떻게 해야 하나요?', '동일한 자료를 구할 수 없을 경우, 유사한 주제의 자료로 변상이 가능합니다. (자료실의 담당 사서와 의논)', '기타');
